package day14;

public class Ex04_Printf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "홍길동";
		int age = 30;

		System.out.printf(name + "의 나이는 " + age + "살 입니다.\n");
		System.out.printf("%s의 나이는 %d살 입니다.", name, age); //s는 문자 d는 정수

	}

}
